import { UPDATE_BORN, ALL_AUTHORS } from '../queries'
import React, { useState } from 'react'
import { useMutation, useQuery } from '@apollo/client'
const Authors = (props) => {
  const [name, setName] = useState('')
  const [born, setBorn] = useState('')
  const [ updateBorn ] = useMutation(UPDATE_BORN,  {
    refetchQueries: [ { query: ALL_AUTHORS }]
  })

  const newBorn = async (event) => {
    event.preventDefault()

    const res = updateBorn({
      variables: { born, name }
    })
    console.log(res)
    setName('')
    setBorn('')
  }

  const res = useQuery(ALL_AUTHORS)
  if(res.loading) {
    return (
      <div>loading...</div>
    )
  }
  const authors = res.data.allAuthors

  if (!props.show) {
    return null
  }

  return (
    <div>
      <h2>authors</h2>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              born
            </th>
            <th>
              books
            </th>
          </tr>
          {authors.map(a =>
            <tr key={a.name}>
              <td>{a.name}</td>
              <td>{a.born}</td>
              <td>{a.bookCount}</td>
            </tr>
          )}
        </tbody>
      </table>
      <form onSubmit={newBorn}>
        <div>
          name
          <input
            value={name}
            onChange={({ target }) => setName(target.value)}
          />
        </div>
        <div>
          born
          <input
            value={born}
            onChange={({ target }) => setBorn(target.value)}
          />
        </div>
        <button type="submit" onSubmit={newBorn}>add author</button>
      </form>
    </div>
  )
}

export default Authors
