import { gql } from '@apollo/client'

export const ALL_AUTHORS = gql`
  query {
    allAuthors {
      name,
      born
    }
  }
`

export const CREATE_BOOK = gql`
mutation addBook ( $title: String!, $author: String!, $published: Int,
  $genres: [String!]! ) {
  addBook( title: $title, author: $author, published: $published,
    genres: $genres) {
    title
    author {
      name
      born
      bookCount
    }
    published
    genres
  }
}
`
export const ALL_BOOKS = gql`
  query {
    allBooks {
      author,
      published,
      title,
      genres
    }
  }
`

export const UPDATE_BORN = gql`
  mutation updateBorn($name: String!, $born: Int!) {
    editAuthor {
      name: $name
      setBornTo: $born
    } {
      name
      born
    }
  }
`
