import React from 'react'
import { ALL_BOOKS, ME } from '../queries'
import { useLazyQuery } from '@apollo/client'
const Recommend = (props) => {
  const [getBooks, result] = useLazyQuery(ALL_BOOKS)
  const userRes = useQuery(ME)
  if( userRes.loading ) {
    return (
      <div>loading...</div>
    )
  }
  const filter = userRes.data.me.favoriteGenre
  getBooks({ variables: { genre: filter }})
  const books = res.data.allBooks
  if (!props.show) {
    return null
  }

  const bookRow = (book) => (
    <tr key={book.title}>
      <td>{book.title}</td>
      <td>{book.author.name}</td>
      <td>{book.published}</td>
    </tr>
  )

  return (
    <div>
      <h2>books</h2>
      books in your favorite genre <b>{filter}</b>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {books.map(book =>
            book.genres.includes(filter) ? bookRow(book) : null
          )}
        </tbody>
      </table>
    </div>
  )
}

export default Recommend
